INSERT INTO `tabPage` VALUES ('Data Import','2009-05-12 11:19:22','2009-07-01 12:10:46','Administrator','Administrator',0,NULL,NULL,NULL,NULL,'<div style=\"margin: 8px\">\n<div class=\'standard_title\'>Import Data</div>\n<table style=\"border-collapse: collapse;\">\n  <tr>\n    <td style=\"width: 60%; border: 1px solid #AAA; padding: 4px;\">\n	<h3 style=\"background-color: #EEF; padding: 2px;\">Step 1. Download Template</h3>\n	<select id=\'import_template\' style=\'margin: 10px;\'>\n		<option>Select...</option>\n	</select>\n	\n	<h3 style=\"background-color: #EEF; padding: 2px;\">Step 2. Upload and Import</h3>\n	<form action=\'cgi-bin/run.cgi\' method=POST enctype=multipart/form-data  target=\"import_Iframe\">\n	<input type=\'hidden\' name=\'cmd\' value=\'import_csv\'>\n	<input type=\'hidden\' name=\'__account\' id=\'acc_id1\'>\n	<table cellspacing=\'10px\' width=\'480px\'>\n		<tr>\n			<td width=\'200px\'>Select CSV File to be imported</td>\n			<td><input type=\'file\' name=\'csv_file\'></td>\n		</tr>\n		<tr>\n			<td width=\'200px\'>Date format in source CSV</td>\n			<td>\n				<select name=\'dateformat\'>\n					<option value=\'yyyy-mm-dd\'>yyyy-mm-dd</option>\n					<option value=\'mm/dd/yyyy\'>mm/dd/yyyy</option>\n					<option value=\'mm/dd/yy\'>mm/dd/yy</option>\n					<option value=\'dd-mm-yyyy\'>dd-mm-yyyy</option>\n				</select>\n			</td>\n		</tr>\n		<tr>\n			<td width=\'200px\'>Over-write records with same name?</td>\n			<td><input type=\'checkbox\' name=\'overwrite\'></td>\n		</tr>\n		<tr>\n			<td></td>\n			<td><div id=\'import_btn\'><input type=\'submit\' value = \'Import\'></div></td>\n		</tr>\n	</table>\n	\n	</form>\n\n	<div style=\'background-color: #FFE; padding: 10px; margin: 10px;\'>\n	<h3>Data Import Guide</h3>\n	<ol>\n		<li>Get the template of the DocType for which you want to import in CSV (Comma seperated values) format.</li>\n		<li>Fill in the data in the template. You can remove columns that are not relevant</li>\n		<li>Save the template in CSV format</li>\n		<li>Select the saved CSV file, identify the date format if any</li>	\n		<li>Click on \"Import\"</li>\n	</ol>\n	</div>\n     </td>\n     <td style=\"border: 1px solid #AAA; padding: 4px;\">\n        <h3>Import Log:</h3>\n        <div id=\"import_result_area\">\n          <iframe name=\"import_Iframe\" src=\"blank.html\" style=\"border: 0px; width: 100%; height: 400px; background-color: #EEE;\">\n        </div>\n     </td>\n   </tr>\n</table>\n</div>','pscript[\'onload_Data Import\'] = function() {\n\n  var sel = $i(\'import_template\');\n\n  $i(\'acc_id1\').value = account_id;\n\n  // load values\n\n  // standard\n  add_sel_options(sel, session.rt);\n\n  // custom\n  // add_sel_option([\'Employee\', \'Leave\']);\n\n  sel.onchange = function() {\n    var sel_get_str = \'Select...\';\n\n    var dt = sel_val($i(\'import_template\'));\n    this.value = sel_get_str;	\n\n    if(dt != sel_get_str)\n      window.location = outUrl + \'?cmd=get_template&__account=\' + account_id + \'&dt=\' + dt;\n  }\n\n}',0,NULL,'',NULL,'System',NULL,NULL,NULL,NULL),('File Browser','2010-03-30 11:15:46','2010-03-10 16:29:12','Administrator','Administrator',0,NULL,NULL,NULL,NULL,'<div style=\"margin: 8px;\">\n<table style=\"border: 1px solid #cccccc; width: 100%; background-color: #dfe1fd; vertical-align: middle; border-collapse: collapse;\" border=\"0\">\n<tbody>\n<tr>\n<td style=\"width: 70%; padding-left: 8px;\">\n<h2>File Browser</h2>\n</td>\n<td style=\"width: 30%; text-align: right; padding-right: 8px; vertical-align: middle;\"><span class=\"link_type\" title=\"Close\"> <img style=\"cursor:pointer\" onclick=\"nav_obj.show_last_open()\" src=\"images/icons/close.gif\" alt=\"\" /> </span></td>\n</tr>\n</tbody>\n</table>\n<div style=\"width: 100%;\">\n<table style=\"border: 1px solid #cccccc; width: 100%; border-collapse: collapse;\" border=\"0\">\n<tbody>\n<tr>\n<td style=\"border: 1px solid #cccccc; padding: 8px; width: 40%; border-collapse: collapse;\">\n<table style=\"width: 1